    @Test
    public void registrarDocumento() {
        logger.debug("DocumentoEJBTest::registrarDocumento");
        PlantillaDTO plantillaDTOTemp = new PlantillaDTO();
        plantillaDTOTemp.setCodigoPlantilla("ABCD");
        PlantillaDTO plantillaDTO = interfazPlantilla.consultarPlantilla(plantillaDTOTemp , null).get(0);

        UsuarioOrganizacionDTO usuarioOrganizacionSesion = new UsuarioOrganizacionDTO();
        usuarioOrganizacionSesion.setIdUsuario(new Long(1));

        // Asigna datos para guardar el documento en la BD
        DocumentoDTO documentoDTO = new DocumentoDTO();
        documentoDTO.setCodigoDocumento("1");
        documentoDTO.setVersionDocumentoCm(1);
        documentoDTO.setPathDocumento("/documento/documento.pdf");
        documentoDTO.setDescripcionDocumento("Descripcion");
        documentoDTO.setFechaGeneracion(new Date());
        documentoDTO.setFechaCreacion(new Date());
        documentoDTO.setPlantillaDTO(plantillaDTO);
        documentoDTO.setUsuarioOrganizacionDTO(usuarioOrganizacionSesion);
        documentoDTO.setConsecutivoDocumento(3L);
        documentoDTO.setVersionDocumento(1);
        documentoDTO.setNombreDocumento("documento.pdf");

        XmlDocumentoDTO xmlDocumentoDTO = new XmlDocumentoDTO();
        xmlDocumentoDTO.setCodigoDocumentoHtml("1");
        xmlDocumentoDTO.setContenidoXml("<html><head><title>Plantilla</title></head><body></body></html>".getBytes(StandardCharsets.UTF_8));
        xmlDocumentoDTO.setNombreXmlDoc(documentoDTO.getNombreDocumento());
        documentoDTO.setXmlDocumentoDTO(xmlDocumentoDTO);
        
        documentoDTO = interfazDocumento.registrarDocumento(documentoDTO);
        logger.info("******* Prueba unitaria registrarDocumento,insertó el documento de id=: "
                + documentoDTO.getIdDocumento());
    }

    @Test
    public void consultarDocumento() {
        logger.debug("DocumentoEJBTest::consultarDocumento");
        DocumentoDTO documentoDTO = new DocumentoDTO();
        documentoDTO.setConsecutivoDocumento(1L);
        documentoDTO.setVersionDocumento(1);
        List<DocumentoDTO> resultados = interfazDocumento.consultarDocumento(documentoDTO);
        Assert.assertTrue(!resultados.isEmpty());
        logger.info("******* Prueba unitaria consultarDocumento, consultó: " + resultados.size());
    }

    @Test
    public void consultarUltimaVersionDocumento() {
        logger.debug("DocumentoEJBTest::consultarUltimaVersionDocumento");
        DocumentoDTO documentoDTO = new DocumentoDTO();
        documentoDTO.setConsecutivoDocumento(1L);
        DocumentoDTO resultado = interfazDocumento.consultarUltimaVersionDocumento(documentoDTO);
        Assert.assertTrue(resultado != null);
        logger.info("******* Prueba unitaria consultarDocumento, consultó: " + resultado.getIdDocumento());
    }
    
    @Test
    public void generarDocumento() {
        logger.debug("DocumentoEJBTest::generarDocumento");
        //Verifica que se esté generando con una fecha anterior o igual a la actual
        Calendar calendarActual = Calendar.getInstance();
        calendarActual.set(Calendar.YEAR, 2015);
        calendarActual.set(Calendar.MONTH, 4);
        calendarActual.set(Calendar.DAY_OF_MONTH, 10);
        // Genera objeto de generacion a partir de entrada
        GeneraDocumentoDTO generaDocumentoDTO = new GeneraDocumentoDTO();
        generaDocumentoDTO.setCodigoPlantilla("ABCDEF");
        
        Map<String, Object> valoresPlantillaMap = new HashMap<String, Object>(); 
        valoresPlantillaMap.put("Nombre Peticionario", "Julio Pinzon");
        valoresPlantillaMap.put("Numero Expediente", 1459);
        valoresPlantillaMap.put("Numero Comparendo", 1000);
        valoresPlantillaMap.put("placa", "DDR014");
        valoresPlantillaMap.put("Fecha comparendo", new Date());
        List<Map<String, Object>> grupo1 = new ArrayList<Map<String, Object>>();
        Map<String, Object> valoresGrupo1_1 = new HashMap<String, Object>();
        valoresGrupo1_1.put("infraccion", "88888888999999888778");
        valoresGrupo1_1.put("Fecha Actual", new Date());
        grupo1.add(valoresGrupo1_1);
        Map<String, Object> valoresGrupo1_2 = new HashMap<String, Object>();
        valoresGrupo1_2.put("infraccion", "6666666665555555");
        valoresGrupo1_1.put("Fecha Actual", new Date());
        grupo1.add(valoresGrupo1_2);
        valoresPlantillaMap.put("grupo_1", grupo1);
        generaDocumentoDTO.setData(valoresPlantillaMap);
        
        generaDocumentoDTO.setFechaGeneracion(calendarActual.getTime());
        generaDocumentoDTO.setDescripcionDocumento("Descripcion");
        generaDocumentoDTO.setFormato(EnumFormatoDocumento.PDF);
        generaDocumentoDTO.setUsuario("diana");
        generaDocumentoDTO.setCarpeta("/documento");
        try {
            DocumentoDTO documento = interfazDocumento.generarDocumento(generaDocumentoDTO);
            logger.info("******* Prueba unitaria generarDocumento,generó el documento de id=: "
                    + documento.getIdDocumento());
            Assert.assertTrue(true);
        } catch (DocumentosException e) {
            logger.error("Error Prueba unitaria generarDocumento", e);
            Assert.assertTrue(false);
        }
    }
    
