    @Test
    public void consultarPlantilla() {
        logger.debug("PlantillaEJBTest::consultarPlantilla");
        PlantillaConsultaDTO plantillaConsultaDTO = new PlantillaConsultaDTO();
        plantillaConsultaDTO.setValidaOrigen(false);
        plantillaConsultaDTO.setUltimaVersion(true);
        PlantillaDTO plantillaDTO = new PlantillaDTO();
        plantillaConsultaDTO.setPlantillaDTO(plantillaDTO);
        List<PlantillaDTO> resultados = interfazPlantilla.consultarPlantilla(plantillaConsultaDTO);
        Assert.assertTrue(!resultados.isEmpty());
        logger.info("******* Prueba unitaria consultarPlantilla, consultó: " + resultados.size());
    }

